from django.shortcuts import redirectfrom .models import User# 사용자 로그인def login_required(function):    def wrap(request, *args, **kwargs):        # print('login_required!')        user = request.session.get('user')        if user is None or not user:            return redirect('/login')        return function(request, *args, **kwargs)    return wrap# admin(관리자)_required 구현def admin_required(function):    def wrap(request, *args, **kwargs):        user = request.session.get('user')        if user is None or not user:            return redirect('/login')        user = User.objects.get(email=user)        if user.level != "admin":            return redirect('/')        return function(request, *args, **kwargs)    return wrap