from django import formsfrom django.contrib.auth.hashers import check_password, make_password# check_password -> 비밀번호와 실제 DB의 인코딩된 비밀번호를 비교해준다.# make_password -> 비밀번호를 그냥 저장하면 안되므로, 해시 된 암호를 만드는 부분from .models import Userclass RegisterForm(forms.Form):    email = forms.EmailField(        error_messages={            'required': '이메일을 입력해주세요'        },        max_length=64, label='이메일'    )    password = forms.CharField(        error_messages={            'required': '비밀번호를 입력해주세요'        },        widget=forms.PasswordInput, label='비밀번호'    )    re_password = forms.CharField(        error_messages={            'required': '비밀번호를 입력해주세요'        },        widget=forms.PasswordInput, label='비밀번호 확인'    )    def clean(self):        clean_data = super().clean()        email = clean_data.get('email')        password = clean_data.get('password')        re_password = clean_data.get('re_password')        if password and re_password:            if password != re_password:                self.add_error('password', '비밀번호가 서로 다릅니다.')                self.add_error('re_password', '비밀번호가 서로 다릅니다.')            else:                user = User(                    email=email,                    password=make_password(password),                )                user.save()class LoginForm(forms.Form):    email = forms.EmailField(        error_messages={            'required': '이메일을 입력해주세요'        },        max_length=64, label='이메일'    )    password = forms.CharField(        error_messages={            'required': '비밀번호를 입력해주세요'        },        widget=forms.PasswordInput, label='비밀번호'    )    def clean(self):        clean_data = super().clean()        email = clean_data.get('email')        password = clean_data.get('password')        if email and password:            try:                user = User.objects.get(email=email)            except User.DoseNotExist:                self.add_error('email', '아이디가 없습니다.')                return            if not check_password(password, user.password):                self.add_error('password', '비밀번호를 틀렸습니다.')            else:                self.email = user.email